'DECLARE',
'    l_query        VARCHAR2(4000); ',
'    l_column_names VARCHAR2(4000);',
'    l_table_names  VARCHAR2(4000);',
'    l_where        VARCHAR2(4000) := '''';',
'    l_where_clause VARCHAR2(4000) := '''';',
'',
'BEGIN',
'      SELECT  ',
'        LISTAGG(DISTINCT ebs_table_name || ''@apps_ebs '' || XX_ERP_INT_UTIL_PKG.generate_alias_name(ebs_table_name), '','') ,',
'        LISTAGG(XX_ERP_INT_UTIL_PKG.generate_alias_name(ebs_table_name) || ''.'' || ebs_column_name, '','') ',
'    INTO ',
'        l_table_names,',
'        l_column_names',
'    FROM ',
'        xx_custom_mappings ',
'    WHERE ',
'        ebs_table_name IS NOT NULL',
'        AND ebs_column_name IS NOT NULL',
'        AND fbdi_identifier = :I2_FBDI_IDENTIFIER;',
'',
'    FOR i IN (',
'        SELECT * FROM xx_custom_mappings ',
'        WHERE MAPPING_TABLE IS NOT NULL ',
'          AND MAPPING_COLUMN IS NOT NULL ',
'          AND fbdi_identifier = :I2_FBDI_IDENTIFIER',
'    )',
'    LOOP',
'        l_where := l_where || '' AND '' || ',
'                   XX_ERP_INT_UTIL_PKG.generate_alias_name(i.EBS_TABLE_NAME) || ''.'' || i.ebs_column_name || ',
'                   '' = '' || ',
'                   XX_ERP_INT_UTIL_PKG.generate_alias_name(i.MAPPING_TABLE) || ''.'' || i.MAPPING_COLUMN;',
'    END LOOP;',
'',
'        FOR rec IN (',
'        SELECT EBS_TABLE_NAME, EBS_COLUMN_NAME, OPERATORS, FILTER_VALUE  ',
'        FROM FILTER_DATA  ',
'    )',
'    LOOP',
'        l_where_clause := l_where_clause || ',
'                          '' AND '' || XX_ERP_INT_UTIL_PKG.generate_alias_name(rec.EBS_TABLE_NAME) || ''.'' || rec.EBS_COLUMN_NAME || ',
'                          '' '' || rec.OPERATORS || ',
'                          '' '''''' || rec.FILTER_VALUE || '''''''';',
'    END LOOP;',
'',
'    ',
'    l_query := ''SELECT '' || LOWER(l_column_names) || ',
'               '' FROM '' || LOWER(l_table_names) || ',
'               '' WHERE 1=1 '' || LOWER(l_where) || LOWER(l_where_clause) || ',
'               '' AND ROWNUM < 500'';',
'',
'  ',
'      -- DBMS_OUTPUT.PUT_LINE(l_query);',
'    return l_query;',
'',
