create table wksp_ebs2cloud_migration.xx_custom_mappings (
    mapping_id                   number default on null wksp_ebs2cloud_migration.xx_custom_mappings_seq.nextval not null enable,
    fbdi_identifier              varchar2(4000 char),
    fusion_template_column       varchar2(4000 char),
    description                  varchar2(4000 char),
    data_type                    varchar2(4000 char),
    field_length                 varchar2(4000 char),
    mandatory_flag               varchar2(4000 char),
    ebs_table_name               varchar2(4000 char),
    ebs_column_name              varchar2(4000 char),
    ebs_column_name_description  varchar2(4000 char),
    ebs_column_name_data_type    varchar2(4000 char),
    ebs_column_name_field_length varchar2(4000 char),
    creation_date                date not null enable,
    created_by                   varchar2(255 char) not null enable,
    last_updation_date           date not null enable,
    updated_by                   varchar2(255 char) not null enable,
    mapping_table                varchar2(240 byte),
    mapping_column               varchar2(120 byte)
);

alter table wksp_ebs2cloud_migration.xx_custom_mappings
    add constraint xx_custom_mappings_mandatory_flag_ck
        check ( mandatory_flag in ( 'Y', 'N' ) ) enable;

alter table wksp_ebs2cloud_migration.xx_custom_mappings
    add constraint xx_custom_mappings_mapping_id_pk primary key ( mapping_id )
        using index enable;


